.experience__container {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 2rem;
  
  }
  
  .experience__container > div {
    background: var(--color-bg-variant);
    padding: 2.5rem 5rem;
    border-radius: 2rem;
    border: 1px solid transparent;
    transition: var(--transition);
    
  }
  
  .experience__container > div:hover {
    background: transparent;
    border-color: var(--color-primary-variant);
    cursor: default;
    
  }
  
  .experience__container > div h3 {
    text-align: center;
    margin-bottom: 2rem;
    color: var(--color-primary);
    
  }
  
  .experience__content {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    row-gap: 2rem;

  }
  
  .experience__details {
    display: flex;
    gap: 1rem;
    
  }
  .experience__details-icon {
    margin-top: 6px;
    color: var(--color-primary);
    margin-left: 25%;
  }
 

/* Queries para convertir en responsives dispositivos medianos (TABLETS)  */

@media screen and (max-width: 1024px)  {
    .experience__container{
        grid-template-columns: repeat(1, 1fr);
    }
    
    .experience__container > div {
        width: 80%;
        padding: 2rem;
        margin: 0 auto;
    }

    .experience__content{
        padding: 1rem;
    }
}
    
/* Queries para convertir en responsives dispositivos peque単os (Telefonos)  */

@media screen and (max-width: 600px)  {
    .experience__container{
        gap: 1rem;
        
    }
    
    .experience__container > div{
        width: 95%;
        padding: 0.5rem 0rem;
    }

    .experience__content {
        padding: 1rem 0rem;
      
      }

    
}
